mapper.select:
select order_id as orderId, student_name as studentName, info, DATE_FORMAT(order_info.create_time,'%Y-%m-%d %H:%i:%s') as createTime, DATE_FORMAT(order_info.motify_time,'%Y-%m-%d %H:%i:%s') as motifyTime, CONCAT(mobile_phone,",",qq,",",wechat,",",phone) as contacts, order_info.channel_id as channelId, channel_name as channelName, order_info.list_source_id as listSourceId, list_source_name as listSourceName, order_info.user_id as userId, user_name as userName, order_info.order_status_id as orderStatusId, order_status_name as orderStatusName, remake
from order_info left join channel on order_info.channel_id = channel.channel_id left join list_source on order_info.list_source_id = list_source.list_source_id left join user on order_info.user_id = user.user_id left join order_status on order_info.order_status_id = order_status.order_status_id
1=1
and order_info.student_name like %#{studentName,jdbcType=VARCHAR}%
and order_info.info like %#{info,jdbcType=VARCHAR}%
and order_info.channel_id = #{channelId,jdbcType=INTEGER}
and order_info.list_source_id = #{listSourceId,jdbcType=INTEGER}
and order_info.user_id = #{userId,jdbcType=INTEGER}
and DATE_FORMAT(order_info.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d')
and DATE_FORMAT(order_info.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
order by order_info.motify_time desc LIMIT #{pageFristItem},#{pageSize}

select count(order_id)
from order_info left join channel on order_info.channel_id = channel.channel_id left join list_source on order_info.list_source_id = list_source.list_source_id left join user on order_info.user_id = user.user_id left join order_status on order_info.order_status_id = order_status.order_status_id
1=1
and order_info.student_name like %#{studentName,jdbcType=VARCHAR}%
and order_info.info like %#{info,jdbcType=VARCHAR}%
and order_info.channel_id = #{channelId,jdbcType=INTEGER}
and order_info.list_source_id = #{listSourceId,jdbcType=INTEGER}
and order_info.user_id = #{userId,jdbcType=INTEGER}
and DATE_FORMAT(order_info.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d')
and DATE_FORMAT(order_info.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
